package aoc2022.day6

fun main() {
    fun partOne() {
        val size = input6.length - 1
        for (first in 0..size) {
            val word = input6.substring(first, first + 4)
            if(word.toSet().size == 4) {
                println(first + 4)
                return
            }
        }
    }

    fun partTwo() {
        val size = input6.length - 1
        for (first in 0..size) {
            val word = input6.substring(first, first + 14)
            if(word.toSet().size == 14) {
                println(first + 14)
                return
            }
        }
    }

    partOne()
    partTwo()
}

const val input6 = """"""